package com.inspur.approval;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.jdbc.datasource.DriverManagerDataSource;import java.io.IOException;/***   @Author Nxy*   @Date 2019/12/27 14:54*   @Param*   @Return*   @Exception*   @Description 数据库工具类*/public class DBTools {    public static JdbcTemplate getSqlSeverJdbcTemplate(String dbUrl, String database, String username, String password){        JdbcTemplate jtSqlSever = null;        DriverManagerDataSource ds = new DriverManagerDataSource();        ds.setDriverClassName("com.microsoft.sqlserver.jdbc.SQLServerDriver");        ds.setUrl("jdbc:sqlserver://" + dbUrl + ";database=" + database + ";user=" + username + ";password=" + password);        ds.setUsername(username);        ds.setPassword(password);        jtSqlSever = new JdbcTemplate(ds);        return jtSqlSever;    }}